import { precacheAndRoute, cleanupOutdatedCaches, createHandlerBoundToURL } from 'workbox-precaching';
import { clientsClaim } from 'workbox-core';
import { registerRoute, NavigationRoute } from 'workbox-routing';

// Version marker for force-update
const SW_VERSION = '2.1.0';

// Precache all assets generated by Vite
// Ajout de ignoreURLParametersMatching pour ignorer les "?v=2"
precacheAndRoute(self.__WB_MANIFEST, {
    ignoreURLParametersMatching: [/.*/]
});

// Cleanup old caches
cleanupOutdatedCaches();

// Allow headers to take control immediately
clientsClaim();

// Garantit que le rafraÃ®chissement des pages fonctionne hors ligne (SPA)
// Utilise index.html comme fallback pour toutes les routes de navigation
const handler = createHandlerBoundToURL('/index.html');
const navigationRoute = new NavigationRoute(handler);
registerRoute(navigationRoute);

// Force immediate activation on install (Aggressive Update)
self.addEventListener('install', () => {
    self.skipWaiting();
});

// Handle SKIP_WAITING message for user-prompted updates (fallback)
self.addEventListener('message', (event) => {
    if (event.data && event.data.type === 'SKIP_WAITING') {
        self.skipWaiting();
    }
});

/**
 * Handle Push Notifications
 */
self.addEventListener('push', (event) => {
    if (!event.data) return;

    try {
        const data = event.data.json();
        const options = {
            body: data.body,
            icon: data.icon || '/logo.jpg',
            badge: '/logo.jpg',
            vibrate: [100, 50, 100],
            data: data.data || {}
        };

        event.waitUntil(
            self.registration.showNotification(data.title || 'Skyjo Score', options)
        );
    } catch (err) {
        console.error('[SW] Push error:', err);
    }
});

/**
 * Handle Notification Clicks
 */
self.addEventListener('notificationclick', (event) => {
    event.notification.close();

    const urlToOpen = event.notification.data?.url || '/';

    event.waitUntil(
        clients.matchAll({ type: 'window', includeUncontrolled: true }).then((windowClients) => {
            // If a window is already open, focus it and navigate
            for (let i = 0; i < windowClients.length; i++) {
                const client = windowClients[i];
                if (client.url === urlToOpen && 'focus' in client) {
                    return client.focus();
                }
            }
            // If no window is open, open a new one
            if (clients.openWindow) {
                return clients.openWindow(urlToOpen);
            }
        })
    );
});